import {Injectable} from "@angular/core";
import {HttpClient, HttpErrorResponse, HttpHeaders, HttpParams} from "@angular/common/http";
import {catchError, Observable, tap, throwError} from "rxjs";
import {Image} from "./image";

@Injectable({
    providedIn: "root"
})
export class DataAccessService {

    private apiEndpoint = "http://localhost:8080/images";

    constructor(private httpClient: HttpClient) {
    }

    imagesById: { [key: string]: Image } = {};
    // imagesForPage: {[key: number]: Image[]} = {};
    // imagesByTagsForPage: {[key: string]: {[key: number]: Image[]}} = {};

    addImageToIndex(image: Image) {
        console.log("added");
        this.imagesById[image.id] = image;
    }

    getImageFromIndexById(id: string) {
        console.log(Object.entries(this.imagesById));
        return this.imagesById[id];
    }

    getImagesPageOf(params: {}, pageNum: string) {
        // Object.values(params)
        //     .forEach(tag => this.imagesByTagsForPage)
        const httpParams = new HttpParams({fromObject: params});
        httpParams.set('num', pageNum);
        console.log(httpParams);

        return this.httpClient
            .get<Image[]>(this.apiEndpoint, {params: httpParams})
            .pipe(
                tap(dataArr => {
                    dataArr.forEach(data => this.addImageToIndex(data))
                }),
                catchError(throwError)
            );
    }

    getImageById(id: string) {
        const headers = new HttpHeaders({"Content-Type": "application/json"});
        return this.httpClient
            .get<Image>(this.apiEndpoint + "/" + id, {headers: headers})
            .pipe(
                tap(data => this.addImageToIndex(data)),
                catchError(throwError)
            );
    }

    categoriseImageUrl(url: string): Observable<Image> {
        const headers = new HttpHeaders({"Content-Type": "application/json"});
        return this.httpClient
            .post<Image>(this.apiEndpoint, url, {headers: headers})
            .pipe(
                tap(data => this.addImageToIndex(data)),
                catchError(this.handlePostRequestError)
            );
    }

    connectionError: string = "Error occurred please try again later.";
    tooManyRequests: string = "Too many requests! Please try again in a minute.";

    private handlePostRequestError(httpErrorResponse: HttpErrorResponse) {
        let errorMessage = "";
        if (httpErrorResponse.error instanceof ErrorEvent) {
            errorMessage = this.connectionError;
        } else {
            if (httpErrorResponse.status === 429) {
                errorMessage = this.tooManyRequests;
            } else if (httpErrorResponse.status === 400 || httpErrorResponse.status === 503) {
                errorMessage = httpErrorResponse.error.error;
            }
        }

        return throwError(() => errorMessage);
    }
}
