import {Injectable} from "@angular/core";
import {NavigationExtras, Router} from "@angular/router";
import {Location} from "@angular/common";

@Injectable({
    providedIn: "root"
})
export class NavigationService {

    constructor(private router: Router, private location: Location) {
    }

    private _isSearchBarVisible: boolean = false;

    private _activeButton: string = '';

    private _inputTag: string = '';

    setNavigationPreferencesWith(isSearchBarVisible : boolean, activeBtnName : string) {
        this.activeButton = activeBtnName;
        this.isSearchBarVisible = isSearchBarVisible;
    }

    applyTagFilter() {
        const queryParams: any = {
            tag: this.inputTag
        };

        const navigationExtras: NavigationExtras = {
            queryParams
        };
        this.router.navigate(['/images'], navigationExtras)
            .catch(err => console.log(err));
        this.location.reload();
    }

    get activeButton(): string {
        return this._activeButton;
    }

    set activeButton(value: string) {
        this._activeButton = value;
    }

    get isSearchBarVisible(): boolean {
        return this._isSearchBarVisible;
    }

    set isSearchBarVisible(value: boolean) {
        this._isSearchBarVisible = value;
    }


    get inputTag(): string {
        return this._inputTag;
    }

    set inputTag(value: string) {
        this._inputTag = value;
    }
}
