import {Component, OnInit} from '@angular/core';
import {NavigationService} from "./shared/navigation.service";
import {NavigationExtras, Router} from "@angular/router";

@Component({
    selector: 'ics-root',
    templateUrl: './app.component.html',
    styleUrls: ['./app.component.css']
})
export class AppComponent {
    title = 'ics';

    constructor(public navigationService: NavigationService, private router: Router) {
    }

    applyTagFilter() {
        const queryParams: any = {
            tag: this.navigationService.inputTag
        };

        const navigationExtras: NavigationExtras = {
            queryParams
        };

        this.router.navigate(['/images'], navigationExtras)
            .catch(err => console.log(err));
    }
}
<header>
	<nav class="navigation">
		<ul class="menu" role="list">
			<li class="menu-item">
				<a routerLink="/"
				   [ngClass]="{'active': navigationService.activeButton === 'submit-page'}">
					Analyse
				</a>
			</li>
			<li class="menu-item">
				<a routerLink="/images"
				   [ngClass]="{'active': navigationService.activeButton === 'gallery-page'}">
					Gallery
				</a>
			</li>
		</ul>
		<div [hidden]="!navigationService.isSearchBarVisible" class="search-bar">
			<input [(ngModel)]="navigationService.inputTag" type="text" placeholder="search by a tag...">
			<button (click)="applyTagFilter()" type="submit">Search</button>
		</div>
	</nav>
</header>
<main>
	<router-outlet></router-outlet>
</main>
