import {Component, OnInit} from "@angular/core";
import {ActivatedRoute, Router} from "@angular/router";
import {DataAccessService} from "../shared/data-access.service";
import {Image} from "../shared/image";

@Component({
    templateUrl: "image-detail.component.html",
    styleUrls: ["image-detail.component.css"]
})
export class ImageDetailComponent implements OnInit {
    constructor(private route: ActivatedRoute, private dataAccessService: DataAccessService, private router: Router) {
    }

    image: Image | undefined;

    imageAlt!: string

    isLandscape: boolean = false;

    ngOnInit(): void {
        let id: string = this.route.snapshot.paramMap.get('id') ?? '';

        this.image = this.dataAccessService.getImageFromIndexById(id);

        if (!this.image) {
            this.dataAccessService.getImageById(id)
                .subscribe({
                    next: (data) => this.image = this.dataAccessService.getImageFromIndexById(data.id),
                    error: () => this.router.navigate([''])
                });
        }

    }

    protected readonly Object = Object;
}
