import {Component, OnInit} from '@angular/core';
import {SearchBarService} from "./shared/search-bar.service";

@Component({
  selector: 'ics-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'ics';

  constructor(public searchBarService: SearchBarService) {
  }

}
<header>
	<nav class="navigation">
<!--		!searchBarService.isSearchBarVisible-->
		<div [hidden]="true" class="search-bar">
			<input type="text" placeholder="search by a tag...">
			<button type="submit">Search</button>
		</div>
		<ul class="menu" role="list">
			<li class="menu-item">
				<a routerLink="" class="active">Analyse</a>
			</li>
			<li class="menu-item">
				<a routerLink="">Gallery</a>
			</li>
		</ul>
	</nav>
</header>
<main>
	<router-outlet></router-outlet>
</main>
