import {Component, OnInit} from "@angular/core";
import {ActivatedRoute, Router} from "@angular/router";
import {DataAccessService} from "../shared/data-access.service";
import {Image} from "../shared/image";

@Component({
    templateUrl: "image-detail.component.html",
    styleUrls: ["image-detail.component.css"]
})
export class ImageDetailComponent implements OnInit {
    constructor(private route: ActivatedRoute, private dataAccessService: DataAccessService, private router: Router) {
    }

    image!: Image;

    imageAlt!: string

    ngOnInit(): void {
        let id: string | null = this.route.snapshot.paramMap.get('id') ?? '';

        this.image = this.dataAccessService.getImageById(id);
        this.imageAlt = Object
                .keys(this.image.tags)
                .join(' ');

        console.log(this.imageAlt);
    }
}
