import {Component, OnDestroy, OnInit} from "@angular/core";
import {FormBuilder, FormGroup, Validators} from "@angular/forms";
import {DataAccessService} from "../shared/data-access.service";
import {ActivatedRoute, Router} from "@angular/router";
import {NavigationService} from "../shared/navigation.service";
import {HttpClient, HttpErrorResponse} from "@angular/common/http";
import {catchError, throwError} from "rxjs";

@Component({
    templateUrl: 'url-submit.component.html',
    styleUrls: ['url-submit.component.css']
})
export class UrlSubmitComponent implements OnInit {

    urlPattern: string =
        "https?:\\/\\/(www\\.)?[-a-zA-Z0-9@:%._\\+~#=]{1,256}\\.[a-zA-Z0-9()]{1,6}\\b([-a-zA-Z0-9()@:%_\\+.~#?&//=]*)";

    errorMessage: string = "";
    hasError: boolean = false;
    isBeingProcessed: boolean = false;


    urlForm!: FormGroup;

    constructor(private formBuilder: FormBuilder, private dataAccessService: DataAccessService, private router: Router,
                private activatedRoute: ActivatedRoute, private navigationService: NavigationService,
                private httpClient: HttpClient) {
        this.navigationService.setNavigationButtonMenuWith('submit-page');
    }


    ngOnInit(): void {
        this.urlForm = this.formBuilder.group({
            url: ["", [Validators.required, Validators.pattern(this.urlPattern)]]
        });
    }

    private handleUrlError(httpErrorResponse: HttpErrorResponse) {
        let errorMessage = "Error1";
        console.log("Inside handdle");

        if (httpErrorResponse.error instanceof ErrorEvent) {
            errorMessage = "Error2";
        } else {
            if (httpErrorResponse.status < 200 || httpErrorResponse.status >= 400) {
                errorMessage = "Error3";
            }
        }

        return throwError(() => errorMessage);
    }

    onSubmit() {
        this.isBeingProcessed = true;
        let url = this.urlForm.value;
        let isImage = undefined;

        this.httpClient.head(url, { observe: 'response' })
            .pipe(catchError(this.handleUrlError))
            .subscribe({
                next: response => {
                    console.log(response);

                    isImage = response
                        .headers
                        .get('Content-Type')
                        ?.startsWith('image');
                },
                error: err => this.supplyErrorToTemplate(err)
            });

        // this.dataAccessService
        //     .categoriseImageUrl(url)
        //     .subscribe({
        //         next: data => {
        //             this.isBeingProcessed = false;
        //             this.router.navigate(['/images', data.id])
        //                 .catch(err => console.log(err));
        //         },
        //         error: err => this.supplyErrorToTemplate(err)
        //     });

    }

    private supplyErrorToTemplate(err: string) {
        this.isBeingProcessed = false;
        this.errorMessage = err;
        this.hasError = true;

        setTimeout(() => {
            this.hasError = false;
        }, 5_000);
    }

}
<h1 class="title">Point to an image to tag or search in gallery</h1>
<form [formGroup]="urlForm" (ngSubmit)="onSubmit()" class="submit-url">
	<div class="supply">
		<label class="url-field-label" for="url-field">
			Provide URL for categorisation
		</label>
		<input class="form-control"
		       id="url-field"
		       type="url"
		       placeholder="https://....."
		       formControlName="url"
		       [ngClass]="{'is-invalid': ((urlForm.get('url')?.touched ||
                                            urlForm.get('url')?.dirty) &&
                                            !urlForm.get('url')?.valid) }"/>
		<span class="invalid-feedback error-msg">
      <span *ngIf="urlForm.get('url')?.errors?.['required']">
              The field is required.
      </span>
      <span *ngIf="urlForm.get('url')?.errors?.['pattern']">
              You must provide a valid URL.
      </span>
    </span>
		<div class="error-msg" *ngIf="hasError">{{errorMessage}}</div>
	</div>
	<div class="submit-ctr">
		<button type="submit" [disabled]="!urlForm.valid || isBeingProcessed">
			<span [textContent]="isBeingProcessed ? '' : 'Analyse'"></span>
			<span *ngIf="isBeingProcessed" class="spinner-border" role="status" aria-hidden="true"></span>
		</button>
	</div>
</form>
