import {Component, OnInit} from "@angular/core";
import {DataAccessService} from "../shared/data-access.service";
import {SearchBarService} from "../shared/search-bar.service";
import {ActivatedRoute, Router} from "@angular/router";
import {Image} from "../shared/image";

@Component({
    templateUrl: "image-gallery.component.html",
    styleUrls: ["image-gallery.component.css"]
})
export class ImageGalleryComponent implements OnInit {

    page: number = 0;
    gallery!: Image[]

    constructor(private dataAccessService: DataAccessService, private searchBarService: SearchBarService,
                private router: Router, private activatedRoute: ActivatedRoute) {
    }

    ngOnInit(): void {
        this.activatedRoute.queryParams.subscribe(params => {
            this.dataAccessService.getImagesPageOf(params)
                .subscribe({
                    next: (dataArr) => {
                        this.gallery = dataArr;
                        this.searchBarService.isSearchBarVisible = true;
                    },
                    error: () => this.router.navigate([''])
                })
        });
    }

    protected readonly Object = Object;
}
