import {Component, OnDestroy, OnInit} from "@angular/core";
import {DataAccessService} from "../shared/data-access.service";
import {NavigationService} from "../shared/navigation.service";
import {ActivatedRoute, NavigationExtras, Router} from "@angular/router";
import {Image} from "../shared/image";
import {BehaviorSubject, Observable} from "rxjs";
import * as lodash from 'lodash';

@Component({
    templateUrl: "image-gallery.component.html",
    styleUrls: ["image-gallery.component.css"]
})
export class ImageGalleryComponent implements OnInit, OnDestroy {

    images: BehaviorSubject<Image[]> = new BehaviorSubject<Image[]>([]);
    finished = false;
    pageNum = 0;
    imagesBatchSize = 12;
    params: { [key: string]: string } = {};

    triggeredOnScroll: boolean = false;

    private _inputTag: string = "";


    get inputTag(): string {
        return this._inputTag;
    }

    set inputTag(value: string) {
        this._inputTag = value;
    }

    constructor(private dataAccessService: DataAccessService, private navigationService: NavigationService,
                private router: Router, private activatedRoute: ActivatedRoute) {
        this.navigationService.setNavigationPreferencesWith(true, 'gallery-page');
    }

    ngOnInit(): void {
        console.log("Init")
        this.activatedRoute.queryParams.subscribe(params => {
            this.params = params;
            this.getImages();
        });
    }

    onScroll() {
        if (this.triggeredOnScroll) {
            console.log('duplicated');
            return;
        }
        this.triggeredOnScroll = true;
        setTimeout(() => this.triggeredOnScroll = false, 5_000);
        console.log("Inside onScroll");
        this.getImages();
    }

    onImgClick(imageId: string) {
        this.router.navigate(['/images', imageId])
            .catch(err => console.log(err));
    }

    getTopThreeTags(image: Image) {
        const tagEntries: [string, number][] = Object.entries(image.tags);
        tagEntries.sort((a, b) => b[1] - a[1]);
        return tagEntries.slice(0, 3);
    }

    filterWithTag() {
        const queryParams = this.activatedRoute.snapshot.queryParams;
        console.log(queryParams);
        const updatedQueryParams = {...queryParams, tag: this.inputTag};
        console.log(updatedQueryParams);
        this.router.navigate(['/images'], {queryParams: {tag: this.inputTag}});
    }

    ngOnDestroy(): void {
        this.images.unsubscribe();
    }

    private getImages() {
        console.log(this.finished);

        if (this.finished) {
            this.pageNum = 0;
            return;
        }

        // let newImages = this.dataAccessService.getImagesPageFromIndex(this.pageNum);
        // if (newImages) {
        //     console.log(this.pageNum + "From scenario 1");
        //     this.determineIfScrollingEnds(newImages);
        //     this.addImagesToBehaviourSubject(this.images, newImages);
        //     this.pageNum++;
        // } else {

        this.dataAccessService
            .getImagesPageOf(this.params, this.pageNum)
            .subscribe({
                next: (dataArr) => {
                    if (dataArr) {
                        this.addToIndices(dataArr, this.pageNum);
                        this.determineIfScrollingEnds(dataArr);
                        this.addImagesToBehaviourSubject(this.images, dataArr);
                        console.log(this.pageNum + "From 2");
                        this.pageNum++;
                    } else {
                        this.finished = true;
                    }
                },
                error: () => console.log("Some error from observer")
            });
        // }
    }

    private addToIndices(dataArr: Image[], pageNum: number) {
        console.log(dataArr);
        dataArr.forEach(data => this.dataAccessService.addImageByIdToIndex(data));
        this.dataAccessService.addImagesPageToIndex(pageNum, dataArr);
    }

    private determineIfScrollingEnds(dataArr: Image[]) {
        if (dataArr.length < this.imagesBatchSize) {
            this.finished = true;
        }
    }

    private addImagesToBehaviourSubject(behaviourSubject: BehaviorSubject<Image[]>, dataArr: Image[]) {
        behaviourSubject.next(lodash.concat(behaviourSubject.getValue(), dataArr));
    }

    protected readonly Object = Object;
}
